mapper.select:
SELECT

FROM cms_fkcontent a

WHERE a.id = #{id}

SELECT

FROM cms_fkcontent a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM cms_fkcontent a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_fkcontent SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
